mapper.update:
UPDATE in_store
SET is_in = #{confirmInStoreDto.isIn}
WHERE ins_id = #{confirmInStoreDto.insId}

mapper.select:
SELECT count(*)
FROM in_store AS t1
JOIN product t2 ON t1.product_id = t2.product_id


        AND t1.store_id = #{inStoreDto.storeId}


        AND t2.product_name like concat('%',#{inStoreDto.productName},'%')


        AND t1.create_time >= #{inStoreDto.startTime}


        AND t1.create_time <= #{inStoreDto.endTime}

SELECT t1.ins_id, t3.store_name, t2.product_name, t1.in_num, t2.in_price, t1.create_by, t1.create_time, t4.user_code, t1.is_in FROM in_store AS t1 JOIN product AS t2 ON t1.product_id = t2.product_id JOIN store AS t3 ON t1.store_id = t3.store_id JOIN user_info AS t4 ON t1.create_by = t4.user_id
AND t1.store_id = #{inStoreDto.storeId}
AND t2.product_name like concat('%',#{inStoreDto.productName},'%')
AND t1.create_time >= #{inStoreDto.startTime}
AND t1.create_time <= #{inStoreDto.endTime}
ORDER BY t1.create_time DESC LIMIT #{page.limitIndex},#{page.pageSize}
